import React, { useState } from 'react';

import Game from '../../shared/pages/Game'
import './Ss7R.css';
import Gamer from '../../shared/components/Gamer'


const Ss7R = props => {
    const [ gameState, setGameState ] = useState({
        players: [
            //for each selected player this...
            {id: 'p1', name: 'acetone7', skore: 0 },
            {id: 'p2', name: 'mom', skore: 0 },
            {id: 'p3', name: 'dad', skore: 0 }
        ],
        round: 1,
        end: false,
        winner: ""
    })

    const onSubmitHandler = e => {
        e.preventDefault()
        //console.log("was submitted update skore with setState")
        if(gameState.round === 1 || gameState.round === 7){
            alert("double score flag")
        }
        if(gameState.round === 7){
            alert("end of series flag")
            gameState.end = true
            alert("print winner with least score")
        }

        //check flags and do for now open
        if(!gameState.end){
            setGameState({
                players: [
                    //for each selected player this...
                    {id: 'p1', name: 'acetone7', skore: 5 },
                    {id: 'p2', name: 'mom', skore: 14 },
                    {id: 'p3', name: 'dad', skore: 0 }
                ],
                end: false,
                round: gameState.round+1
            })
        }
        else{
            setGameState({
                players: [
                    //for each selected player this...
                    {id: 'p1', name: 'acetone7', skore: 5 },
                    {id: 'p2', name: 'mom', skore: 14 },
                    {id: 'p3', name: 'dad', skore: 0 }
                ],
                round: gameState.round+1,
                end: true,
                winner: "babyKris"
            })
        }




    }
    if(!gameState.end){
        return (
            <div className="game-container">
                <form onSubmit={onSubmitHandler}>

                    <h4 className="round_num">game {gameState.round}</h4>
                    <Gamer id={gameState.players[0].id} name={gameState.players[0].name} skore={gameState.players[0].skore} />
                    <Gamer id={gameState.players[1].id} name={gameState.players[1].name} skore={gameState.players[1].skore} />
                    <Gamer id={gameState.players[2].id} name={gameState.players[2].name} skore={gameState.players[2].skore} />
                <button type="submit" className="game-button" >Done!</button>
                </form>
            </div>
        )
    }
    else{
        return (
            <div className="game-container">
                <h4 className="round_num">end of series</h4>
                <h2>winner is {gameState.winner}</h2>
            </div>
        )
    }

}


export default Ss7R;
