import React, { useState } from 'react';

//import Game from '../../shared/pages/Game'
import './Ss7R.css';
import Gamer from '../../shared/components/Gamer'


const Ss7R = props => {
    const [ gameState, setGameState ] = useState({
        players: [
            //for each selected player this...
            {id: 'p1', name: props.selected[0], skore: 0 },
            {id: 'p2', name: props.selected[1], skore: 0 },
            {id: 'p3', name: props.selected[2], skore: 0 }
        ]
    })
    const [ winner, setWinner ] = useState({
        player: "acetone7",
        skore: 0
    })
    const [ gameRound, setGameRound ] = useState(1)
    const [ end, setEnd ] = useState(false)

    const onSubmitHandler = e => {
        e.preventDefault()
        //console.log("was submitted update skore with setGameState")
        if(gameRound === 1){
            //console.log("double score flag")
        }
        if(gameRound === 7){
            //alert("end of series flag")
            setEnd(true)
            //alert("print winner with least score")

        }
        //check flags and do for now open
        if(!end){
            //total the scores and add a round
            setGameState({
                players: [
                    //for each selected player this...
                    {id: 'p1', name: "acetone7", skore: 45 },
                    {id: 'p2', name: "babykris", skore: gameState.players[1].skore },
                    {id: 'p3', name: "baby", skore: gameState.players[2].skore }
                ]
            })
            setGameRound(gameRound+1)
        }
        else{
            console.log(end)

        }
    }
    const updateSkores = (e) => {
        const f=e.target.id
        const g=parseInt(e.target.value)
        console.log("f=: ", f," g=: ", g)
        let tempArray = [] //store the updated list of skores
        gameState.players.map(player => {
            //for each player push the skore to a new temp array
            tempArray.push(player.skore)
        })
        gameState.players.map(player => {
            //for each player push the skore to a new temp array
            if (player.id === f ){
                let total = player.skore + g
                tempArray.push(total)
            }
            else{
                //add old skore itself
                tempArray.push(player.skore)
            }
        })
        setGameState({
            players: [
                //for each selected player this...
                {id: 'p1', name: "acetone7", skore: tempArray[0] },
                {id: 'p2', name: "babykris", skore: tempArray[1] },
                {id: 'p3', name: "baby", skore: tempArray[2] }
            ]
        })

    }

    let least_skore = winner.skore
    let least_player = winner.player

    return (
        <div className="game-container">
        {
            end ?
                <div className="result">
                <h4 className="round_num">end of series</h4>
                <h2>winner is {least_player} with {least_skore} points</h2>
                </div>
             :


            <form onSubmit={onSubmitHandler}>
                <h4 className="round_num">game {gameRound}</h4>
                <Gamer id={gameState.players[0].id} name={gameState.players[0].name} skore={gameState.players[0].skore} changed={updateSkores} />
                <Gamer id={gameState.players[1].id} name={gameState.players[1].name} skore={gameState.players[1].skore} changed={updateSkores} />
                <Gamer id={gameState.players[2].id} name={gameState.players[2].name} skore={gameState.players[2].skore} changed={updateSkores} />
            <button type="submit" className="game-button" >NEXT</button>
            </form>

        }
        </div>
    )

}

export default Ss7R;
