import React, { useState } from 'react';

//import Game from '../../shared/pages/Game'
import './Ss7R.css';
import Gamer from '../../shared/components/Gamer'


const Ss7R = props => {
    const [ gameState, setGameState ] = useState({
        players: [
            //for each selected player this...
            {id: 'p1', name: props.selected[0], skore: 0 },
            {id: 'p2', name: props.selected[1], skore: 0 },
            {id: 'p3', name: props.selected[2], skore: 0 }
        ]
    })
    const [ winner, setWinner ] = useState({
        player: "acetone7",
        skore: 0
    })
    const [ gameRound, setGameRound ] = useState(1)
    const [ end, setEnd ] = useState(false)

    const onSubmitHandler = e => {
        e.preventDefault()
        //console.log("was submitted update skore with setState")
        if(gameRound === 1 || gameState.round === 7){
            alert("double score flag")
        }
        if(gameRound === 7){
            //alert("end of series flag")
            setEnd(true)
            //alert("print winner with least score")

        }
        //check flags and do for now open
        if(!end){
            //total the scores and add a round
            setGameState({
                players: [
                    //for each selected player this...
                    {id: 'p1', name: "acetone7", skore: 5 },
                    {id: 'p2', name: "anu", skore: 14 },
                    {id: 'p3', name: "roopa", skore: 0 }
                ]
            })
            setEnd(false)
            setGameRound(gameRound+1)
            setWinner({
                player: "",
                skore: 0
            })
        }
        else{
            /*
            setGameState({
                players: [
                    //for each selected players this...
                    {id: 'p1', name: "acetone7", skore: 5 },
                    {id: 'p2', name: "anu", skore: 14 },
                    {id: 'p3', name: "roopa", skore: 0 }
                ],
                //round: gameState.round+1,
                //end: true,
                //winner: "babykris"
            })*/
            setEnd(true)
            setWinner("babykris")
        } 
    }
    const updateSkores = (e) => {
        //alert(e.target.value)
        //alert(e.target.id)

        const f=e.target.id
        const g=e.target.value
        console.log("f=: ", f," g=: ", g)
        let tempArray = [] //store the updated list of skores
        gameState.players.map(player => {
            //for each player push the skore to a new temp array
            if (player.id === f){
                //set skore new
                //player.skore = e.target.value
                /*
                setGameState({
                    players: [
                        //for each selected players this...
                        {id: 'p1', name: "acetone7", skore: 5 },
                        {id: 'p2', name: "anu", skore: 14 },
                        {id: 'p3', name: "roopa", skore: 0 }

                    ]
                })*/
                if(gameRound === 1 || gameRound === 7){
                    tempArray.push(g * 2)
                }
                else {
                    tempArray.push(g)
                }
            }
            else{
                //add old skore itself
                tempArray.push(player.skore)
            }
        })
        setGameState({
            players: [
                //for each selected player this...
                {id: 'p1', name: "acetone7", skore: tempArray[0] },
                {id: 'p2', name: "anu", skore: tempArray[1] },
                {id: 'p3', name: "roopa", skore: tempArray[2] }
            ]
        })

    }
    if(!end){
        return (
            <div className="game-container">
                <form onSubmit={onSubmitHandler}>

                    <h4 className="round_num">game {gameRound}</h4>
                    <Gamer id={gameState.players[0].id} name={gameState.players[0].name} skore={gameState.players[0].skore} changed={updateSkores} />
                    <Gamer id={gameState.players[1].id} name={gameState.players[1].name} skore={gameState.players[1].skore} />
                    <Gamer id={gameState.players[2].id} name={gameState.players[2].name} skore={gameState.players[2].skore} />
                <button type="submit" className="game-button" >NEXT</button>
                </form>
            </div>
        )
    }
    else{
        let least_skore = winner.skore
        let least_player = winner.player
        gameState.players.map(player => {
            if (player.skore < least_skore){
                least_skore = player.skore
                least_player = player.name
            }
        })
        setWinner({
            player: least_player,
            skore: least_skore
        })
        return (
            <div className="game-container">
                <h4 className="round_num">end of series</h4>

                <h2>winner is {winner.player} with {winner.skore} points</h2>
            </div>
        )
    }

}


export default Ss7R;
