import React, { useState } from 'react';
import './Jackpot.css'

import Gamer from '../../shared/components/Gamer'

const Jackpot = (props) => {
    const [ gang, setGang ] = useState(false)
    const [ stakes, setStakes ] = useState(100)
    const [ config, setConfig ] = useState(true)

    let AllPlayerNames = []
    props.all_players.map(player => {
        AllPlayerNames.push(player.name)
    })


    const [ playing, setPlaying ] = useState(AllPlayerNames)


    const gangFlagHandler = () => {
        if (gang === true){
            setGang(false)
        }
        else{
            setGang(true)
        }
    }

    const stakesInputHandler = (e) => {
        setStakes(e.target.value)
    }

    const beginJackpotHandler = (e) => {
        setConfig(false)
        if(gang === true){
            //all 5 render
            /*
            props.all_players.map(player => {
                //console.log(player.name)
            })
            */
           setPlaying(AllPlayerNames)
           //console.log(AllPlayerNames)
        }
        else{
            let tempArray = []
            let newPlayingState = []
            //render only the first 3 players from jackpot
            props.all_players.map(player => {
                tempArray.push(player.name)
            })
            let x = 0
            for(x; x<3; x++){
                //console.log(tempArray[x])
                newPlayingState.push(tempArray[x])

            }
            setPlaying(newPlayingState)
        }
    }
    if(config){
        const style = {
            color: '#e3c43b'
        }
        return (
            <div className="game-container  ">

                <h3>Jackpot</h3>
                <label><b>stakes: </b></label><label style={style}><b>{stakes}</b></label>
                <input type="text" name="stakes" className="game-input1" id="stakes_input" value={stakes} onChange={stakesInputHandler} />
                <br />
                <label><b>gang flag: </b></label><label style={style}><b>{gang ? "ON" : "OFF" }</b></label>
                {gang ?
                <button value="reset" onClick={gangFlagHandler} className="game_flag_button">reset</button>
                :
                <button value="set" onClick={gangFlagHandler} className="game_flag_button" >set</button>
                }
                <button className="game-button jack" onClick={beginJackpotHandler}>START</button>
            </div>

        )
    }
    else{
        if(gang){
            const housefull = {
                color: '#666666'
            }
            return (
                //full team block render
                <div className="game-container">
                    <div className="game">
                        <h3>Jackpot {stakes}</h3> <h5 style={housefull}>{gang ? "full house" : "" }</h5>
                        <div className="game-container">
                            <Gamer id={props.all_players[0].id} name={props.all_players[0].name} skore={props.all_players[0].skore} />
                            <Gamer id={props.all_players[1].id} name={props.all_players[1].name} skore={props.all_players[1].skore} />
                            <Gamer id={props.all_players[2].id} name={props.all_players[2].name} skore={props.all_players[2].skore} />
                            <Gamer id={props.all_players[3].id} name={props.all_players[3].name} skore={props.all_players[3].skore} />
                            <Gamer id={props.all_players[4].id} name={props.all_players[4].name} skore={props.all_players[4].skore} />
                        </div>

                    </div>
                </div>

            )
        }
        else{
            return (
                //usual team block render
                <div className="game-container">
                    <div className="game">
                    <h3>Jackpot {stakes} {gang ? "Full House" : "" }</h3>
                        <div className="game-container">
                            <Gamer id={props.all_players[0].id} name={props.all_players[0].name} skore={props.all_players[0].skore} />
                            <Gamer id={props.all_players[1].id} name={props.all_players[1].name} skore={props.all_players[1].skore} />
                            <Gamer id={props.all_players[2].id} name={props.all_players[2].name} skore={props.all_players[2].skore} />
                        </div>

                    </div>
                </div>

            )
        }

    }

};

export default Jackpot;
