            }
            else{
                //add old skore itself
            }
        })
    }
    if(!gameState.end){
        return (
            <div className="game-container">
                <form onSubmit={onSubmitHandler}>

                    <h4 className="round_num">game {gameState.round}</h4>
                    <Gamer id={gameState.players[0].id} name={gameState.players[0].name} skore={gameState.players[0].skore} changed={updateSkores} />
                    <Gamer id={gameState.players[1].id} name={gameState.players[1].name} skore={gameState.players[1].skore} />
                    <Gamer id={gameState.players[2].id} name={gameState.players[2].name} skore={gameState.players[2].skore} />
                <button type="submit" className="game-button" >NEXT</button>
                </form>
            </div>
        )
    }
    else{
        return (
            <div className="game-container">
                <h4 className="round_num">end of series</h4>
                <h2>winner is {gameState.winner}</h2>
            </div>
        )
    }

}


