import React, { useState } from 'react'

import './Gamer.css'

const Gamer = props => {
    const [state, setState] = useState({
        player_skore: 0
    })

    return (
        <li className="game_list_item" id={props.id}>
            <h3 className="gamer_name">{props.name}</h3>
            <h4 className="cumulative_skore">total: {props.skore} </h4>
            <input type="text" className="current_skore_input" name={props.id + "skore"} value={state.player_skore} onChange={props.updateSkoreInputHandler}  />
            <img src={require("../res/green_tick_nowhite.png")} className="approval_icon" />
        </li>
    )
}

export default Gamer
