import React, { useState } from 'react';

import Game from '../../shared/pages/Game'
import './Ss7R.css';

const Ss7R = () => {
    var iteration = 1
    const defaultPlayers = [
        {id: 'p1', name: 'acetone7', skore: 0 },
        {id: 'p2', name: 'mom', skore: 0 },
        {id: 'p3', name: 'dad', skore: 0 }
    ]
    const [game_num, setSkores] = useState({runn: 1})

    const updateSkores = () => {
        if(game_num.runn > 7){
            return
        }
        if (game_num.runn === 1){
            //double the score
            //increase iteration by 1
            //alert(game_num.runn+" doubled score")
            setSkores({runn: game_num.runn+1})
        }
        else if(game_num.runn === 7){
            //double and update scores for all players
            //print winner with least score
            //idk what to do when clash happens i never faced it in real life till now
            //end
            //alert("game over score doubled")
            setSkores({runn: game_num.runn+1})
        }
        else{
            //increase iteration by 1
            // update scores
            setSkores({runn: game_num.runn +1})
            alert(game_num.runn)
        }

    }

    return (
        <div className="game-container">
            <Game playing={defaultPlayers}/>
            <button className="game-button" onClick={() => updateSkores(iteration)}>Done!</button>
        </div>
    )
};


export default Ss7R;
