import React, { useState } from 'react';

import Game from '../../shared/pages/Game'
import './Ss7R.css';

const Ss7R = () => {
    var iteration = 1
    const defaultPlayers = [
        {id: 'p1', name: 'acetone7', skore: 0 },
        {id: 'p2', name: 'mom', skore: 0 },
        {id: 'p3', name: 'dad', skore: 0 }
    ]
    const [skores, setSkores] = useState({runn: 1})

    const updateSkores = iter => {
        if (iter === 1){
            //double the score
            //increase iteration by 1

            setSkores({runn: skores.runn +1})
            alert(skores.runn)
            alert(iter)
            iteration = iter + 1
        }
        else if(iteration === 7){
            //double and update scores for all players
            //print winner with least score
            //idk what to do when clash happens i never faced it in real life till now
            //end
            alert("game over")
        }
        else{
            //increase iteration by 1
            // update scores
            iteration += 1
            setSkores({runn: skores.runn +1})
            alert(skores.runn)
        }

        iteration = iteration + 1

    }

    return (
        <div className="game-container">
            <Game playing={defaultPlayers}/>
            <button className="game-button" onClick={() => updateSkores(iteration)}>Done!</button>
        </div>
    )
};


export default Ss7R;
