import React, { useState } from 'react';

import Game from '../../shared/pages/Game'
import './Ss7R.css';
import Gamer from '../../shared/components/Gamer'


const Ss7R = props => {
    const [ gameState, setGameState ] = useState({
        players: [
            //for each selected player this...
            {id: 'p1', name: 'acetone7', skore: 0 },
            {id: 'p2', name: 'mom', skore: 0 },
            {id: 'p3', name: 'dad', skore: 0 }
        ],
        round: 1
    })

    return (
        <div className="game-container">
            <form onSubmit={this.onSubmitHandler}>
                {//<Game playing={defaultPlayers}/>}
                }
                <h4 className="round_num">game {this.state.round}</h4>
                <Gamer id={this.state.players[0].id} name={this.state.players[0].name} skore={this.state.players[0].skore} />
                <Gamer id={this.state.players[1].id} name={this.state.players[1].name} skore={this.state.players[1].skore} />
                <Gamer id={this.state.players[2].id} name={this.state.players[2].name} skore={this.state.players[2].skore} />
            <button type="submit" className="game-button" >Done!</button>
            </form>
        </div>
    )
}


export default Ss7R;


