import React, { useState } from 'react';
import './Ss7.css';
import PlayersList from '../../shared/components/PlayersList';
import PLAYERS from '../../shared/LocalPlayers';
import Ss7R from './Ss7R';




{/* here i wanna put the game logic i guess and store the results in database after game is completed */}
{/* it has to take input 7 times for each player and add up each time player total */}
{/* first and last game points doubled */}
const Ss7 = () => {
    const [all_players, setSelectedPlayers] = useState(PLAYERS);


    const beginSs7 = form_event => {
        //take the players marked as playing and render the table with three slots
        alert("game triggered render page with selected players in template");
        form_event.preventDefault();
        console.log(form_event.target.player_items)
        form_event.target.player_items.map(player => {
            if(player.isChecked === true){
                console.log(player.id)
            }
        })

    };

    return (
        <div className="game-container">
            <div className="game">
                <h3>ss7</h3>
                <h5>Add Players</h5>
                {/* display all the players contained in the array as checkboxes */}
                <form onSubmit={beginSs7}>
                <PlayersList player_items={PLAYERS}/>
                <button 
                className="begin-game-button"
                type="submit"
                >
                begin!
                </button>
                </form>
            </div>
        </div>

    );
};

export default Ss7;
